const swiperElement = document.querySelector(".main-banner .swiper-container");
const swiperWrapper = swiperElement.querySelector(".swiper-wrapper");

const swiperSlides = swiperElement.querySelectorAll(".swiper-slide");
swiperSlides.forEach((slide,_) => {
  const clonedSlide = slide.cloneNode(true);
  swiperWrapper.appendChild(clonedSlide);
});

const swiper = new Swiper(swiperElement, {
  slidesPerView: 1,
  spaceBetween: 0,
  // autoplay: {
  //   delay: 7000,
  // },
  loop: false,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
});

const swiperElementPartners = document.querySelector(".works .slide .swiper-container");
const swiperPartners = new Swiper(swiperElementPartners, {
  spaceBetween: 30,
  autoplay: {
    delay: 7000,
  },
  loop: false,
  navigation: {
    nextEl: ".swiper-button-next",
    prevEl: ".swiper-button-prev"
  },
  breakpoints: {
    375: {
      slidesPerView: 1,
    },

    1024: {
      slidesPerView: 2,
    },
  }
});